rule.GoodCode:
	import java.util.*;
import java.security.*;

class AssignmentOperator_Good { 
    bool isValid(int value) {
        int x=10;
        if (value==100) {
        	System.out.println("\n Valid Assignment Operator in If Statement");
        }
        while (a == b) {
            System.out.println("\n Valid Assignment Operator in While Statement");
      }
    }
}

rule.BadCode:
 
	 import java.util.*;
import java.security.*;

class AssignmentOperator_Bad { 
    bool isValid(int value) {
        int x=10;
        if (value=100) {
        	System.out.println("\n Not valid Assignment Operator in If Statement");
        }
        while (a = b) {
            System.out.println("\n Not valid Assignment Operator in While Statement");
      }
    }
}

